import React from 'react';

const App = () => {
  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
      <h1>Blog App</h1>
      <p>The application is now connected to the server.</p>
      <p>MongoDB is connected successfully.</p>
      
      <div style={{ marginTop: '20px', padding: '15px', backgroundColor: '#f0f0f0', borderRadius: '5px' }}>
        <h2>Next Steps</h2>
        <ol>
          <li>Register a new user account</li>
          <li>Create your first blog post</li>
          <li>Test the comment functionality</li>
        </ol>
      </div>
    </div>
  );
};

export default App;
